import React from 'react';
import ReactDOM from 'react-dom';
import { createStore, combineReducers } from 'redux';
import AppRouter from './routers/AppRouter';
import {v4 as uuid} from 'uuid';



// Action creator
const addBlog = ({title="" ,description="",dateAdded=0})=>({
    type: "ADD_BLOG",
    blog: {
        id: uuid(),
        title: title,
        description: description,
        dateAdded: dateAdded
    }
});

const removeBlog = ({id})=>(
    {
        type:"REMOVE_BLOG",
        id: id
    }
)


const blogState = []

const blogsReducer  = (state = blogState, action) =>{
    switch(action.type){
        case "ADD_BLOG":
            return [
                ...state,
                action.blog
            ]
            case "REMOVE_BLOG":
            return state.filter()
        default:
            return state;
    }
};

const authState = {};

const authReducer = (state = authState, action) =>{
    switch(action.type){
        default:
            return state;
    }
}



const store = createStore(
    combineReducers({
        blogs: blogsReducer,
        auth: authReducer
    })
)

store.subscribe(()=>{
    console.log(store.getState());
});


store.dispatch(addBlog({title:"b1",description:"desc1"}));
store.dispatch(addBlog({title:"b2",description:"desc2", dateAdded: Date.now()}));










const App = ()=>{
    return(
        <div>
            <AppRouter />
        </div>
    )
}



ReactDOM.render(<App />, document.getElementById('root'));
