import React from 'react';
import ReactDOM from 'react-dom';
import { createStore, combineReducers } from 'redux';
import AppRouter from './routers/AppRouter';

const state ={
    blogs: [
        {
            id:1,
            title: 'blog title 1',
            description: 'blog description 1',
            dateAdded: 0
        }
    ],
    auth:{
        userid:1,
        username: 'hasanbakhtiar',
        email: "hasan@webluna.az"
    }
}


// Action creator
const addBlog = ()=>({
    type: "ADD_BLOG",
    blog:{
            id:2,
            titile: 'aa',
            description: 'bb',
            dateAdded:0
    }
})


const blogState = []

const blogsReducer  = (state = blogState, action) =>{
    switch(action.type){
        default:
            return state;
    }
};

const authState = {};

const authReducer = (state = authState, action) =>{
    switch(action.type){
        default:
            return state;
    }
}



const store = createStore(
    combineReducers({
        blogs: blogsReducer,
        auth: authReducer
    })
)

store.subscribe(()=>{
    console.log(store.getState());
});













const App = ()=>{
    return(
        <div>
            <AppRouter />
        </div>
    )
}



ReactDOM.render(<App />, document.getElementById('root'));
