import { createStore, combineReducers, applyMiddleware } from 'redux';
import blogsReducer from '../reducers/blogs';
import authReducer  from '../reducers/auth';
import thunk from 'redux-thunk';

export default ()=>{
    const store = createStore(
        combineReducers({
            blogs: blogsReducer,
            auth: authReducer
        })
    )

    return store;

}
